package com.evergent.corejava.generalprog;

public class swapwt {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int a=10,b=20;
		System.out.println("Before Swap a is "+a+"and b is "+b);
		a=a+b;
		b=a-b;
		a=a-b;
		System.out.println("Before Swap a is "+a+"and b is "+b);

	}

}
